import React, { useEffect, useState } from "react";
import SpeechRecognition, {
  useSpeechRecognition,
} from "react-speech-recognition";
import mute from "../images/mute.svg";
import micophone from '../images/microphone.gif'
function Modal(props) {
    const [string, setstring] = useState(second)
  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition,
    interimTranscript,
    finalTranscript,
  } = useSpeechRecognition();


  useEffect(() => {
    if (transcript&&listening) {
      props.handleChange(transcript);
    }else if(finalTranscript&&!listening){
        props.handleSubmit()
        resetTranscript()
        props.isClose()
    }
  }, [transcript,listening]);

  useEffect(()=>{
if(props.open){
    setTimeout(() => {
        SpeechRecognition.startListening()
    }, 1000);
}
  },[props.open])
  if (!browserSupportsSpeechRecognition) {
    return <span>Browser doesn't support speech recognition.</span>;
  }
  return (
    <div
      id="myModal"
      className={`${props.open ? "modal-open" : "modal-close"}`}
    >
      <div className="modal-content">
        <span className="close" onClick={() => props.isClose()}>
          &times;
        </span>
        <div>
          <p>Microphone: {listening ? "on" : "off"}</p>
          <div className="mic-main">
            <div>
                <h3>

            {transcript}
                </h3>
            </div>
            <div>
              {
                listening?<div><img src={micophone} width='400'/></div>:<div><img onClick={SpeechRecognition.stopListening} src={mute} width='300'/></div>
              }
            </div>
          </div>


        </div>
      </div>
    </div>
  );
}

export default Modal;
